void diagnose(bool i_has_osa){
    if (!diagnosed){ 

    if (i_has_osa){
        Dhas_osa = true;
    } else {
    endOfTheLine=true;
        Dno_osa = true;
    }
    diagnosed = 1;   
    corDig = Dhas_osa == has_osa;
    corPosDig= has_osa && corDig;
    }
}

