int calcAge(int ageLoc){
    int i=0;
    int calcAge = 0;

    for(i=0;i<lGroups;i++){
        int v = fGroups[i];
        if(gender == MALE){
            v = mGroups[i];
        }
        if(ageLoc > v){
            calcAge = aGroups[i];
        } else {
            if(ageLoc > 0 && v > 0){
                calcAge = calcAge + (ageLoc*5 / v);
            }
        }
        ageLoc = ageLoc - v;
    }
    return calcAge;
}

int calcAhi(int num){
    int z[3] = yAhi;
    int i =0;
    int ans = 0;
    int l = 0;
    if(bmi>30){
        z=ybAhi;
    }
    if(age >= 50){
        z = oAhi;
        if(bmi > 30){
            z = obAhi;
        }
    } 

    for( i=0;i<lAhi;i++){
        if(num > z[i]){
            ans = vAhi[i];
        } else {
           if(num > 0 && z[i] > 0){
                if(i>0){
                  l = vAhi[i-1];      
                }
                ans = ans + (vAhi[i]-l)*num/z[i];
            }
        }
        num = num - z[i];
    }
    return ans;
}

Gen lbmi(){
    Gen z[4] = fBmi;
    int a = 0;
    int i=0;
    if(gender==MALE){
        z = mBmi;
    }
    for(i=0;i<lBmi;i++){
        if(age > aBmi[i]){
           a = i;         
        }
    }

    return z[a];
}

double gbmi(){
    return rni(lbmi());
}
void calcSleepy(int val){

    int old = 0;
    int ahiLevel =0;
    if(age>50){
        old = 1;
    }

    if (ahi > 5){
        ahiLevel = 2;
    }
    if(ahi > 15){
        ahiLevel = 4;
    }
    sleepy = (sleepyGen[(ahiLevel + old)] < val);
}
